#!/usr/bin/ruby

# samples
# bvwbjplbgvbhsrlpgdmjqwftvncz: first marker after character 5
# nppdvjthqldpwncqszvftbrmjlhg: first marker after character 6
# nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg: first marker after character 10
# zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw: first marker after character 11

@test = ''
#@test = 'zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw'

puts @test.chars.each_cons(4).find_index{ |a| a.uniq.count == 4} + 4
puts @test.chars.each_cons(14).find_index { |a| a.uniq.count == 14 } + 14
exit

@marker = []
@iter = 0
@start_index = -1
@test.each_char do | item|

  if @marker.length == 4
    puts "we have 4 at position #{@iter}"
    puts "@marker #{@marker} position #{@iter} first marker at: #{@start_index}"
    exit
  end

  if @marker.include?(item)
    puts "@marker includes #{item} resetting marker iter: #{@iter}"
    @marker = []
    #@marker << item
    @start_index = @iter
  else
    puts "add item to @marker #{item}"
    @marker << item
    puts "@marker #{@marker}"
  end
  @iter += 1
end

# 1261 too low
puts "@marker #{@marker} position #{@iter} first marker at: #{@start_index}"
